import React, {Component} from "react";
import {Redirect} from "react-router-dom";
import "../css/newPayment.css";

import caretDown from "../images/caret_down.png";
import bank from "../images/bank.png";
import payment from "../images/payment.png";

import TableSeparator from "./TableSeparator";
import InputPanel from "./InputPanel";
import InputPanelWithCurrencies from "./InputPanelWithCurrencies";
import InputPanelWithConfirmation from "./InputPanelWithConfirmation";

export default class NewPayment extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Parametry nové platby
            payment: {
                name: "",
                mark: "-",
                accountNumber: "",
                amount: 0,
                currency: "CZK",
                variableSymbol: 0,
                constantSymbol: 0,
                specificSymbol: 0,
                paymentDate: new Date(),
                paymentType: "Platba bankovním převodem",
            },

            // Úspěšná platba
            successfulPayment : false,

            // Chybové zprávy
            nameError: "",
            accountNumberError: "",
            amountError: "",
            variableSymbolError: "",
            constantSymbolError: "",
            specificSymbolError: "",
            confirmationError: "",

            // Seznam bankovních kódů
            bankCodes: [],

            // Prefix čísla účtu
            accountNumberPrefix: "",

            // Bankovní kód
            bankCode: "",
            codeIsSelected: false,

            // Potvrzovací kód
            confirmationGenerated: Math.floor(Math.random() * (99999 - 10000 + 1) + 10000),
            confirmationInput: 0,
        }

        this.setBankCode = this.setBankCode.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {
        document.title = "Nová platba | Web Bank";

        // Request - vrací kódy bank
        fetch("http://localhost:8080/api/bank-codes").then(response => response.json()
            .then(data => this.setState({
                bankCodes: data,
        })));
    }

// Změna stavu kódu banky //////////////////////////////////////////////////////////

    setBankCode(event) {

        // Nastavení kódu banky, při kliknutí
        // Přepnutí true / false
        if (event.target.id === "bankCode") {

            this.setState({
                bankCode: event.target.attributes.name.value,
                codeIsSelected: !this.state.codeIsSelected,
            });

        // Přepnutí true / false
        } else {

            this.setState({
                codeIsSelected: !this.state.codeIsSelected,
            });
        }
    }

// Odeslání formuláře /////////////////////////////////////////////////////////////

    handleSubmit(event) {
        event.preventDefault();

        // 
        this.refs.confirmationInput.value = "";

        const confirmationGenerated = this.state.confirmationGenerated;
        const confirmationInput = parseInt(this.state.confirmationInput)

        // Složení bankovního účtu
        const accountNumber = this.state.accountNumberPrefix + "/" + this.state.bankCode;

        this.setState({

            payment: {
                ...this.state.payment,
                accountNumber: accountNumber,
            }
        }, () => {

            // 
            fetch("http://localhost:8080/api/new-payment", {

                method: "POST",
                headers: {"Content-Type": "application/json"},

                body: JSON.stringify(this.state.payment),

            }).then(response => response.json().then(data => {

                // Úspěšná platba
                if (response.ok) {

                    this.setState({
                        successfulPayment: true,
                    });

                } else {

                    // 
                    let confirmationError = "Ověřovací kód není správný";
                    if (confirmationGenerated === confirmationInput) {

                        confirmationError = "";
                    }

                    // 
                    let accountNumberError = data.accountNumber;
                    if (this.state.payment.accountNumber.length === 11 &&
                        this.state.bankCode === "") {

                        accountNumberError = "Kód banky musí být zadán";
                    }

                    // Změna stavu chybových zpráv
                    this.setState({
                        nameError: data.name,
                        accountNumberError: accountNumberError,
                        amountError: data.amount,
                        variableSymbolError: data.variableSymbol,
                        constantSymbolError: data.constantSymbol,
                        specificSymbolError: data.specificSymbol,

                        confirmationError: confirmationError,
                        confirmationGenerated: Math.floor(Math.random() * (99999 - 10000 + 1) + 10000),
                    });
                }
            }));
        });
    }

// Změna stavu zadaných údajů /////////////////////////////////////////////////////

handleChange(event) {

    const name = event.target.name;
    let value = event.target.value;

    if (name === "accountNumberPrefix" || name === "confirmationInput") {
        
        this.setState({
            [name]: value,
        });

    } else {

        // 
        if (value.includes(",")) {

            value = value.replace(",", ".");
        }

        this.setState({

            payment: {
                ...this.state.payment,
                [name]: value,
            }
        });
    }
}

// Vykreslení /////////////////////////////////////////////////////////////////////

    render() {

        // 
        if ( this.state.successfulPayment) {
            return <Redirect to="/prehled" />;
        }

        return(
            <div id="newPaymentContainer">

                <form id="newPayment" onSubmit={this.handleSubmit}>

                    <img id="payment" src={payment} alt="Payment" />

                    <h1>Nová platba</h1>
                    <hr/> <br/>

                    <table>
                        <tbody>

                            <TableSeparator label="Povinné údaje" />

                            <InputPanel name="name" label="Název platby:" placeholder="Nájem bytu" 
                                error={this.state.nameError} onChange={this.handleChange} />

                            <tr>
                                <td>
                                    <label htmlFor="accountNumberPrefix">Číslo účtu:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.accountNumberError}</div>

                                    <input id="accountNumberPrefix" placeholder="7253962689" type="text" 
                                           name="accountNumberPrefix" onChange={this.handleChange} />

                                    <div id="bankCodesSelect" onClick={this.setBankCode}>
                                        <div>
                                            <div>{this.state.bankCode}</div>

                                            <img id="bank" src={bank} alt="Bank" 
                                                 className={(this.state.bankCode) ? "hide" : ""} />

                                            <img id="caretDown" src={caretDown} alt="Caret Down"
                                                 className={(this.state.bankCode) ? "hide" : ""} />
                                        </div>

                                        <div id="bankCodesChoice" className={(this.state.codeIsSelected) ? "show" : "hide"}>
                                            {this.state.bankCodes.map((item, index) => 
                                                
                                                <div id="bankCode" key={index} onClick={this.setBankCode} name={item.code}>
                                                    {item.code} - {item.name}
                                                </div>
                                            )} 
                                        </div>
                                    </div>
                                </td>
                            </tr>

                            <InputPanelWithCurrencies name="amount" label="Částka:" placeholder="0,00" 
                                error={this.state.amountError} onChange={this.handleChange} />


                            <TableSeparator label="Nepovinné údaje" />


                            <InputPanel name="variableSymbol" label="Variabilní symbol:" placeholder="5795504032" 
                                error={this.state.variableSymbolError} onChange={this.handleChange} />


                            <InputPanel name="constantSymbol" label="Konstantní symbol:" placeholder="5296841057" 
                                error={this.state.constantSymbolError} onChange={this.handleChange} />


                            <InputPanel name="specificSymbol" label="Specifický symbol:" placeholder="4398956257" 
                                error={this.state.specificSymbolError} onChange={this.handleChange} />


                            <TableSeparator label="Ověření" />

                            
                            <tr>
                                <td>
                                    <label htmlFor="confirmationInput">Ověřovací kód:</label>
                                </td>

                                <td>
                                    <div id="confirmationError">{this.state.confirmationError}</div>

                                    <input id="confirmationInput" 
                                           ref="confirmationInput"
                                           type="text"
                                           name="confirmationInput"
                                           onChange={this.handleChange} 
                                    />

                                    <div id="confirmationValue">{this.state.confirmationGenerated}</div>
                                </td>
                            </tr>
                        </tbody>
                    </table>

                    <button type="submit">Odeslat platbu</button>
                </form>
            </div>
        )
    }
}
