import React, {Component} from "react";

import NumberFormatter from "../NumberFormatter";

export default class InputPanelExchangeRate extends Component {

// Konstruktor /////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Směnný kurz na CZK
            exchangeRateToCZK: 1,
        }
    }

    componentDidUpdate() {

        console.log(this.props.currency);
    }

// Vykreslení //////////////////////////////////////////////////////////////

    render() {

        // Zobrazení směnného kurzu
        const showExchangeRate = (this.props.currency === "CZK" ? false : true);

        return(
            <tr className={(showExchangeRate ? "" : "hide")}>
                <td></td>
                
                <td id="exchangeRate">
                    <div>{this.props.label}&nbsp;
                        <b>{(this.props.amount * this.state.exchangeRateToCZK)} CZK</b>
                    </div>
                </td>
            </tr>
        )
    }
}
