import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: [],

            detail: false,
        }

        this.switchDetail = this.switchDetail.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

    switchDetail(event, index) {

        console.log(event.target);

        this.setState({
            detail: (this.state.detail ? false : true),
        });
    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        let year = new Date().getFullYear();

        let showDetail = (this.state.detail ? "show" : "hide");
        console.log(showDetail);

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <details id="payment" key={index} onClick={this.switchDetail(index)}>
                        <summary>
                            <table>
                                <tbody>
                                    <tr>
                                        <td id="column1" rowSpan="2">
                                            <div id="paymentDate">{item.paymentDate.replace(year, "")}</div>
                                        </td>

                                        <td id="column2">
                                            <div id="name">{item.name}</div>
                                        </td>

                                        <td id="column3">
                                            <div id="amount" className={(item.amount.includes("+") ? "plus" : "minus")}>
                                                {item.amount} {item.currency}
                                            </div>
                                        </td>  
                                    </tr>

                                    <tr>
                                        <td id="paymentType">
                                            <div>{item.paymentType}</div>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </summary>

                        <div id="detail">
                            <div>
                                <b>Variabilní symbol:</b> {item.variableSymbol}
                            </div>

                            <div>
                                <b>Konstantní symbol:</b> {item.constantSymbol}
                            </div>

                            <div>
                                <b>Specifický symbol:</b> {item.specificSymbol}
                            </div>

                            <div>
                                <b>ID transakce:</b> {item.id}
                            </div>

                            <div>
                                <b>Datum platby:</b> {item.paymentDate}
                            </div>
                        </div>
                    </details>
                )}

            </div>
        )
    }

}
