import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: [],

            detail: false,
        }

        this.createRow = this.createRow.bind(this)
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

    createRow(label, number) {

        if (number > 0) {

            return  <tr>
                        <td id="column1"></td>
                        <td id="column2">
                            <b>{label}:</b>
                        </td>

                        <td id="column3">
                            {number}
                        </td>
                    </tr>
        }
    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        let year = new Date().getFullYear();

        let showDetail = (this.state.detail ? "show" : "hide");
        console.log(showDetail);

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <details id="payment" key={index}>
                        <summary>
                            <table>
                                <tbody>
                                    <tr>
                                        <td id="column1" rowSpan="2">
                                            <div id="paymentDate">{item.paymentDate.replace(year, "")}</div>
                                        </td>

                                        <td id="column2">
                                            <div id="name">{item.name}</div>
                                        </td>

                                        <td id="column3" rowSpan="2">
                                            <div id="amount" className={(item.amount.includes("+") ? "plus" : "minus")}>
                                                {item.amount} {item.currency}
                                            </div>
                                        </td>  
                                    </tr>

                                    <tr>
                                        <td>
                                            <div id="paymentType">{item.paymentType}</div>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </summary>

                        <div id="detail">
                            <table>
                                <tbody>
                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Variabilní symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.variableSymbol}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Konstantní symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.constantSymbol}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Specifický symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.specificSymbol}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>ID transakce:</b>
                                        </td>

                                        <td id="column3">
                                            {item.id}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Datum platby:</b>
                                        </td>

                                        <td id="column3">
                                            {item.paymentDate}
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </details>
                )}

            </div>
        )
    }

}
