import React, {Component} from "react";

export default class InputPanelWithBankCode extends Component {

// Konstruktor //////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Seznam bankovních kódů
            bankCodes: [],
        }
    }

// Získání seznamu bankovních kódů ///////////////////////////////////////////////

    componentDidMount() {

        // Request - vrací kódy bank
        fetch("http://localhost:8080/api/bank-codes").then(response => response.json()
        .then(data => this.setState({
            bankCodes: data,
        })));
    }

// Vykreslení ////////////////////////////////////////////////////////////////////

    render() {

        return(
            <tr>
            <td>
                <label htmlFor="accountNumberPrefix">Číslo účtu:</label>
            </td>

            <td>
                <div id="error">{this.state.accountNumberError}</div>

                <input id="accountNumberPrefix" placeholder="7253962689" type="text" 
                       name="accountNumberPrefix" onChange={this.handleChange} />

                <div id="bankCodesSelect" onClick={this.setBankCode}>
                    <div>
                        <div>{this.state.bankCode}</div>

                        <img id="bank" src={bank} alt="Bank" 
