import React, {Component} from "react";
import {Redirect} from "react-router-dom";

import logo from "../images/logo.png";
import "../loginPage/loginPage.css";

import OverviewIndex from "../overviewPage/OverviewIndex";

export default class LoginForm extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////////////

    constructor(props) {
        
        super(props)

        this.state = {

            // Přihlašovací údaje
            clientNumber: "",
            password: "",

            // Chybové zprávy
            clientNumberError: "",
            passwordError: "",

            // Uživatel
            user: {
                id: 0,
                name: "",
                surname: "",
                email: "",
                clientNumber: 0,
                password: "",
                balance: 0,
                currency: "",
                accountNumber: "",
            },

            successLogin: false,
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

// Změna stavu ///////////////////////////////////////////////////////////////////////

    handleChange(event) {

        this.setState({
            [event.target.name]: event.target.value
        });
    }

// Odeslání formuláře ////////////////////////////////////////////////////////////////

    handleSubmit(event) {

        event.preventDefault();

        // Request
        fetch("http://localhost:8080/api/login", {

            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(this.state)
            
        }).then(response => response.json().then(data => {

            // Změna stavu uživatele
            if (response.ok) {

                this.setState({
                    user: data.user,
                    successLogin: true,
                });

            } else {

                // Změna stavu chybových zpráv
                this.setState({
                    clientNumberError: data.clientNumber,
                    passwordError: data.password,
                });
            }
        }));
    }

// Vykreslení ////////////////////////////////////////////////////////////////////////

    render() {

       // Přesměrování na stránku přehledu
       if (this.state.successLogin) {

            this.props.history.push("/overview");
            return <OverviewIndex user={this.state.user} />
        }

        return (
            <form onSubmit={this.handleSubmit}>

                <img id="logo" src={logo} alt="Logo" />

                <table>
                    <tr>
                        <td>
                            <label htmlFor="clientNumber">Klientské číslo: </label>
                        </td>

                        <td>
                            <div id="error">{this.state.clientNumberError}</div>

                            <input id="clientNumber" name="clientNumber" autoFocus="autoFocus" 
                                onChange={this.handleChange} />
                        </td>
                    </tr>

                    <tr>
                        <td>
                            <label htmlFor="password">Heslo: </label>
                        </td>

                        <td>
                            <div id="error">{this.state.passwordError}</div>

                            <input type="password" name="password" id="password"
                                onChange={this.handleChange} />
                        </td>
                    </tr>
                    
                    <tr>
                        <td colSpan="2">
                            <button id="submit" type="submit">Pokračovat</button>
                        </td>
                    </tr>
                </table>
            </form>
        )
    }
}
