import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: []
        }
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        let year = new Date().getFullYear();

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <div id="payment" key={index}>
                        <table>
                            <tbody>
                                <tr>
                                    <td id="column1" rowSpan="2">
                                        <div id="paymentDate">{item.paymentDate.replace(year, "")}</div>
                                    </td>

                                    <td id="column2">
                                        <div id="name">{item.name}</div>
                                    </td>

                                    <td id="column3">
                                        <div id="amount" className={(item.amount.includes("+") ? "plus" : "minus")}>
                                            {item.amount} {item.currency}
                                        </div>
                                    </td>  
                                </tr>

                                <tr>
                                    <td id="paymentType">
                                        <div>{item.paymentType}</div>
                                    </td>
                                </tr>

                                <div>
                                    <div>Variabilní symbol:
                                        <div>{item.variableSymbol}</div>
                                    </div>

                                    <div>Konstantní symbol:
                                        {item.constantSymbol}
                                    </div>

                                            <div>Specifický symbol:
                                                {item.specificSymbol}
                                            </div>

                                            <div>ID transakce:
                                                {item.id}
                                            </div>

                                            <div>Datum platby:
                                                {item.paymentDate}
                                            </div>
                                </div>
                            </tbody>
                        </table>
                    </div>
                )}

            </div>
        )
    }

}
