import React, {Component} from "react";

import logo from "../image/logo.png";
import "../css/loginPage.css";

export default class LoginForm extends Component {

    constructor(props) {
        
        super(props)

        this.state = {
            clientNumber: "",
            password: "",

            clientNumberError: "",
            passwordError: "",
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {

        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {

        event.preventDefault();

        fetch("http://localhost:8080/api/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(this.state)
        }).then(response => {

            if (response.ok) {
                console.log("Response Ok");
            } else response.json().then(data => this.setState({
                clientNumberError: data.clientNumber,
                passwordError: data.password,
                
            }));
        });
    }

    render() {

        return (
            <form onSubmit={this.handleSubmit}>

                <img id="logo" src={logo} alt="Logo" />

                <table>
                    <tr>
                        <td>
                            <label htmlFor="clientNumber">Klientské číslo: </label>
                        </td>

                        <td>
                            <input id="clientNumber" name="clientNumber" autoFocus="autoFocus" 
                                onChange={this.handleChange} />
                        </td>
                    </tr>

                    <tr>
                        <td>
                            <label htmlFor="password">Heslo: </label>
                        </td>

                        <td>
                            <input type="password" name="password" id="password"
                                onChange={this.handleChange} />
                        </td>
                    </tr>
                    
                    <tr>
                        <td colSpan="2">
                            <button id="submit" type="submit">Pokračovat</button>
                        </td>
                    </tr>
                </table>
            </form>
        )
    }
}
