export const DateFormatter = (dateString) => {

    const date = new Date(dateString);

    let year = date.getFullYear();
    let month = date.getMonth() + 1;
    let day = date.getDate()

    if (month < 10) {
        month = "0" + month;
    }

    if (day < 10) {
        day = "0" + day;
    }

    return day + "." + month + "." + year;
}

export default DateFormatter;
