import React, {Component} from "react";

export default class InputPanel extends Component {

// Vykreslení ////////////////////////////////////////////////////////////////////

    render() {

        return(
            <tr>
                <td>
                    <label htmlFor="name">Název platby:</label>
                </td>

                <td>
                    <div id="error">{this.state.nameError}</div>

                    <input id="name" 
                            placeholder="Nájem bytu" 
                            type="text" 
                            name="name"
                            onChange={this.handleChange}
                    />
                </td>
            </tr>
        )
    }
}
