import React, {Component} from "react";

import safe from "../images/safe.png";

export default class AccountInfo extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Uživatel
            user: {
                id: this.props.userID,
                name: "",
                surname: "",
                email: "",
                clientNumber: 0,
                password: "",
                balance: 0,
                currency: "",
                accountNumber: "",
            },

            // Aktuální kurzy
            currencies: {
                EUR: 0,
                USD: 0,
            },

            // Aktivní kurzy
            activeCurrency: {
                CZK: true,
                EUR: false,
                USD: false,
            }
        }

        this.changeCurrency = this.changeCurrency.bind(this);
    }

    componentDidMount() {

        // Request - vrycí seznam aktuálních kurzů
        fetch("https://api.exchangeratesapi.io/latest?base=CZK")
            .then(response => response.json().then(data => this.setState({
                currencies: {
                    EUR: data.rates.EUR,
                    USD: data.rates.USD,
                } 
            })));

        // Request - vrací uživatele, podle ID
        fetch("http://localhost:8080/api/user/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                user: data,
            })));
    }

    changeCurrency(event) {
        console.log(event.target.value);
    }

    render() {

        let isCZK = (this.state.activeCurrency.CZK ? "active" : "");
        let isEUR = (this.state.activeCurrency.EUR ? "active" : "");
        let isUSD = (this.state.activeCurrency.USD ? "active" : "");

