        return(
            <div id="account">

                <img src={safe} alt="Safe" />

                <div id="user">{this.state.user.name} {this.state.user.surname}</div>
                <div id="accountNumber">{this.state.user.accountNumber}</div>

                <div>Aktuální zůstatek:
                    <div id="balance">{this.state.user.balance} {this.state.user.currency}</div>
                </div>

                <div id="buttonPanel">
                    <input type="button" className={isCZK} onClick={this.changeCurrency} value="CZK" />
                    <input type="button" className={isEUR} onClick={this.changeCurrency} value="EUR" />
                    <input type="button" className={isUSD} onClick={this.changeCurrency} value="USD" />
                </div>
            </div>
        )
    }

}
