import React, {Component} from "react";

//
import DetailTable from "./DetailTable";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: [],
        }

        this.showMorepayments = this.showMorepayments.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        let date = new Date();

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID 
            + "/year=" + date.getFullYear() + "&month=" + (date.getMonth() + 1))
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

    showMorepayments() {

        const length = this.state.payments.length;
        const lastDate = this.state.payments[length - 1].paymentDate.split(".");

        console.log(lastDate);

        // let payment = {
        //     id: 25,
        //     name: "ADD TEST",
        //     mark: "+",
        //     amount: "7 259,50",
        //     currencry: "CZK",
        //     variableSymbol: 1248759214,
        //     constantSymbol: 6548,
        //     specificSymbol: 8762895541,
        //     paymentDate: "03.08.2020",
        //     paymentType: "Platba kartou",
        // }

        // let payments = this.state.payments;
        // payments.push(payment);

        // this.setState({
        //     payments: payments,
        // });

    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        let year = new Date().getFullYear();

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <details id="payment" key={index}>
                        <summary>
                            <table>
                                <tbody>
                                    <tr>
                                        <td id="column1" rowSpan="2">
                                            <div id="paymentDate">{item.paymentDate.replace(year, "")}</div>
                                        </td>

                                        <td id="column2">
                                            <div id="name">{item.name}</div>
                                        </td>

                                        <td id="column3" rowSpan="2">
                                            <div id="amount" className={(item.mark.includes("+") ? "plus" : "minus")}>
                                                {item.mark}{item.amount} {item.currency}
                                            </div>
                                        </td>  
                                    </tr>

                                    <tr>
                                        <td>
                                            <div id="paymentType">{item.paymentType}</div>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </summary>

                        <DetailTable item={item} />

                    </details>
                )}

                <div id="buttonContainer">
                    <button onClick={this.showMorepayments}>Načíst starší</button>
                </div>

            </div>
        )
    }

}
