import React, {Component} from "react";

export default class InputPanelWithConfirmation extends Component {

    render() {

        return(
            <tr>
                <td>
                    <label htmlFor={this.props.confirmationInput}>Ověřovací kód:</label>
                </td>

                <td>
                    <div id="confirmationError">{this.state.confirmationError}</div>

                    <input id={this.props.confirmationInput} 
                           ref={this.props.confirmationInput}
                           type="text"
                        name={this.props.confirmationInput}
                        onChange={this.handleChange} 
                    />

                    <div id="confirmationValue">{this.state.confirmationGenerated}</div>
                </td>
            </tr>
        )
    }
}
