import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: []
        }
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        for (let index of this.state.payments) {
            console.log(index.amount.includes("+") ? "plus" : "minus");
        }

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <div id="payment" key={index}>
                        <table>
                            <tbody>
                                <tr>
                                    <td id="column1" rowSpan="2">
                                        <div id="paymentDate">{item.paymentDate}</div>
                                    </td>

                                    <td id="column2">
                                        <div id="name">{item.name}</div>
                                    </td>

                                    <td id="column3">
                                        <div id="amount">{item.amount} {item.currency}</div>
                                    </td>
                                </tr>

                                <tr>
                                    <td id="paymentType">
                                        <div>{item.paymentType}</div>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                )}

            </div>
        )
    }

}
