import React, {Component} from "react";

import safe from "../images/safe.png";

export default class AccountInfo extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Uživatel
            user: {
                id: this.props.userID,
                name: "",
                surname: "",
                email: "",
                clientNumber: 0,
                password: "",
                balance: 0,
                currency: "",
                accountNumber: "",
            },

            // Aktuální kurzy
            currencies: {
                CZK: 0,
                EUR: 0,
                USD: 0,
            },

            // Aktivní kurzy
            activeCurrency: {
                CZK: true,
                EUR: false,
                USD: false,
            }
        }

        this.changeCurrency = this.changeCurrency.bind(this);
    }

    componentDidMount() {

        // Request - vrací seznam aktuálních kurzů
        fetch("https://api.exchangeratesapi.io/latest?base=CZK")
            .then(response => response.json().then(data => this.setState({

                currencies: {
                    CZK: data.rates.CZK,
                    EUR: data.rates.EUR,
                    USD: data.rates.USD,
                } 
            })));

        // Request - vrací uživatele, podle ID
        fetch("http://localhost:8080/api/user/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                user: data,
            })));
    }

    changeCurrency(event) {

        this.setState({
            activeCurrency: {
                [event.target.value]: true,
            },
        });
    }

    numberFormatter(number) {
        return number.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, " ")
            .replace(".", ",");
    }

    render() {

        // Změna třídy tlačítka
        let isCZK = (this.state.activeCurrency.CZK ? "active" : "");
        let isEUR = (this.state.activeCurrency.EUR ? "active" : "");
        let isUSD = (this.state.activeCurrency.USD ? "active" : "");

        let balance = parseFloat(this.state.user.balance);
        let currency = this.state.user.balance;

        const activeCurrency = this.state.activeCurrency;

        Object.keys(this.state.activeCurrency).map(key => {

            if (activeCurrency[key]) {
                balance = balance * this.state.currencies[key];
                currency = key;
            }
        });

        return(
            <div id="account">

                <img src={safe} alt="Safe" />

                <div id="user">{this.state.user.name} {this.state.user.surname}</div>
                <div id="accountNumber">{this.state.user.accountNumber}</div>

                <div>Aktuální zůstatek:
                    <div id="balance">{this.numberFormatter(balance.toFixed(2))} {currency}</div>
                </div>

                <div id="buttonPanel">
                    <input type="button" className={isCZK} onClick={this.changeCurrency} value="CZK" />
                    <input type="button" className={isEUR} onClick={this.changeCurrency} value="EUR" />
                    <input type="button" className={isUSD} onClick={this.changeCurrency} value="USD" />
                </div>
            </div>
        )
    }

}
