import React, {Component} from "react";

import Months from "./Months"

export default class BalanceGraphs extends Component {

// Konstruktor //////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            width: 340,
            height: 100,
            currentMonth: 0,

            monthsSum: [],
        }
    }

    componentDidMount() {

        fetch("http://localhost:8080/api/payments/sum/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                monthsSum: data,
                currentMonth: new Date().getMonth() + 1,
        })));

        const income = 90;
        const costs = 20;

        console.log(this.state.monthsSum.length);

        const context = this.refs.canvas.getContext("2d");

        context.fillStyle = "#76A21E";
        context.fillRect(18, this.state.height - income, 20, income);

        context.fillStyle = "#B22222";
        context.fillRect(42, this.state.height - costs, 20, costs);
    }

// Vykreslení ////////////////////////////////////////////////////////////////////

    render() {

        console.log(this.state.monthsSum.length);

        return(
            <div id="graphs">
                <canvas id="canvas" 
                        ref="canvas"
                        width={this.state.width} 
                        height={this.state.height} 
                />

                <table>
                    <tbody>
                        <tr>
                            <td>
                                {Months[this.state.currentMonth - 2]}
                            </td>

                            <td>
                                {Months[this.state.currentMonth - 1]}
                            </td>

                            <td>
                                {Months[this.state.currentMonth]}
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}
