import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: []
        }
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request
        fetch("http://localhost:8080/api/payments/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                payments: data,
            })));

    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <div id="payment" key={index}>
                        <table>
                            <tbody>
                                <tr>
                                    <td id="column1" rowSpan="2">
                                        <div id="paymentDate">{item.paymentDate}</div>
                                    </td>

                                    <td id="column2">
                                        <div id="name">{item.name}</div>
                                    </td>

                                    <td id="column3">
                                        <div id="amount" className={(item.amount.includes("+") ? "plus" : "minus")}>
                                            {item.amount} {item.currency}
                                        </div>
                                    </td>  
                                </tr>

                                <tr>
                                    <td id="paymentType">
                                        <div>{item.paymentType}</div>
                                    </td>
                                </tr>

                                <tr>
                                    <td colSpan="3">
                                        <div>Variabilní symbol:
                                            {item.variableSymbol}
                                        </div>
                                    </td>
                                </tr>

                                <tr>
                                    <td colSpan="3">
                                        <div>Konstantní symbol:
                                            {item.constantSymbol}
                                        </div>
                                    </td>
                                </tr>

                                <tr>
                                    <td colSpan="3">
                                        <div>Specifický symbol:
                                            {item.specificSymbol}
                                        </div>
                                    </td>
                                </tr>

                                <tr>
                                    <td>
                                        <div>ID transakce
                                            {item.id}
                                        </div>
                                    </td>
                                </tr>

                                <tr>    
                                    <td>
                                        <div>Datum platby
                                            {item.paymentDate}
                                        </div>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                )}

            </div>
        )
    }

}
