import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Redirect, Switch} from "react-router-dom";
import LoginForm from "./loginPage/LoginForm";
import OverviewIndex from "./overviewPage/OverviewIndex";

export default class App extends Component {

  constructor(props) {

    super(props);

    this.state = {

      userID: 0,
    }

    this.setUser = this.setUser.bind(this);
  }

  setUser(data) {

    console.log(data);

    this.setState({
      userID: data,
    });
  }

  render() {
    return (
      <Router>
        {/* <LoginForm setUser={this.setUser} /> */}
        {/* <OverviewIndex userID={this.state.userID} /> */}
        <Route path="/login" component={() => <LoginForm setUser={this.setUser} />} />
        <Route path="/overview" component={() => <OverviewIndex userID={this.state.userID} />} />

        <Redirect from="/" to="/login" />
      </Router>
    )
  }
}
