import React, {Component} from "react";

import safe from "../images/safe.png";

export default class AccountInfo extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Uživatel
            user: {
                id: this.props.userID,
                name: "",
                surname: "",
                email: "",
                clientNumber: 0,
                password: "",
                balance: 0,
                currency: "",
                accountNumber: "",
            },
        }
    }

    componentDidMount() {

        // Request
        fetch("http://localhost")
    }

    render() {

        return(
            <div id="account">

                <img src={safe} alt="Safe" />

                <div id="user">{this.state.user.name} {this.state.user.surname}</div>
                <div id="accountNumber">{this.state.user.accountNumber}</div>

                <div>Aktuální zůstatek:
                    <div id="balance">{this.state.user.balance} {this.state.user.currency}</div>
                </div>
            </div>
        )
    }

}
