import React, {Component} from "react";
import {Link} from "react-router-dom";
import "./css/navigationPanel.css";

export default class NavigationPanel extends Component {

    constructor(props) {
        super(props);

        this.logout = this.logout.bind(this);
    }

    logout() {

        this.props.setUser(0);
    }

    render() {

        const hideOverviewLink = (window.location.pathname === "/prehled" ? true : false);

        return(
            <div id="navigationContainer">

                <Link id="overview" className={(window.location.pathname === "/prehled")} to="/prehled" >Přehled</Link>
                <Link id="logout" to="/prihlaseni" onClick={this.logout}>Odhlášení</Link>
                
            </div>
        )
    }
}
