import React, {Component} from "react";

export default class PaymentHistory extends Component {

// Konstruktor ////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            payments: [],
        }

        this.showMorepayment = this.showMorepayment.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {

        let date = new Date();

        // Request
        // fetch("http://localhost:8080/api/payments/" + this.props.userID 
        //     + "/year=" + date.getFullYear() + "&month=" + (date.getMonth() + 1))
        //     .then(response => response.json().then(data => this.setState({
        //         payments: data,
        //     })));

        const payment = {
            id: 20,
            name: "TEST",
            mark: "-",
            amount: "-2 000,50",
            currencry: "CZK",
            variableSymbol: 1234567890,
            constantSymbol: 1234,
            specificSymbol: 5987654321,
            paymentDate: "03.08.2020",
            paymentType: "Platba kartou",
        }

        this.setState({
            payments: [payment],
        });

    }

    showMorepayment() {

        let payment = {
            id: 25,
            name: "ADD TEST",
            mark: "+",
            amount: "+7 259,50",
            currencry: "CZK",
            variableSymbol: 1248759214,
            constantSymbol: 6548,
            specificSymbol: 8762895541,
            paymentDate: "03.08.2020",
            paymentType: "Platba kartou",
        }

        let payments = this.state.payments;
        payments.push(payment);

        console.log()

        this.setState({
            payments: payments,
        });

    }

// Vykreslení //////////////////////////////////////////////////////////////////////

    render() {

        let year = new Date().getFullYear();

        if (this.state.payments.length !== 0) {
            console.log(this.state.payments[0]);
        }

        return(
            <div id="history">

                {this.state.payments.map((item, index) =>

                    <details id="payment" key={index}>
                        <summary>
                            <table>
                                <tbody>
                                    <tr>
                                        <td id="column1" rowSpan="2">
                                            <div id="paymentDate">{item.paymentDate.replace(year, "")}</div>
                                        </td>

                                        <td id="column2">
                                            <div id="name">{item.name}</div>
                                        </td>

                                        <td id="column3" rowSpan="2">
                                            <div id="amount" className={(item.amount.includes("+") ? "plus" : "minus")}>
                                                {item.amount} {item.currency}
                                            </div>
                                        </td>  
                                    </tr>

                                    <tr>
                                        <td>
                                            <div id="paymentType">{item.paymentType}</div>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </summary>

                        <div id="detail">
                            <table>
                                <tbody>
                                    <tr className={(item.variableSymbol === 0 ? "hideEmptyRow" : "")}>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Variabilní symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.variableSymbol}
                                        </td>
                                    </tr>

                                    <tr className={(item.constantSymbol === 0 ? "hideEmptyRow" : "")}>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Konstantní symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.constantSymbol}
                                        </td>
                                    </tr>

                                    <tr className={(item.specificSymbol === 0 ? "hideEmptyRow" : "")}>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Specifický symbol:</b>
                                        </td>

                                        <td id="column3">
                                            {item.specificSymbol}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>ID transakce:</b>
                                        </td>

                                        <td id="column3">
                                            {item.id}
                                        </td>
                                    </tr>

                                    <tr>
                                        <td id="column1"></td>
                                        <td id="column2">
                                            <b>Datum platby:</b>
                                        </td>

                                        <td id="column3">
                                            {item.paymentDate}
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </details>
                )}

                <button onClick={this.showMorepayment}>Načíst více</button>

            </div>
        )
    }

}
