import React, {Component} from "react";
import graph from "../images/graph.png";

import Months from "./Months";

export default class PaymentsReport extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            income: 0,
            costs: 0,

            currentMonth: "",
        }
    }

    componentDidMount() {

        const date = new Date();
        this.setState({
            currentMonth: Months[date.getMonth() + 1],
        });

        // Request
        fetch("http://localhost:8080/api/payments/sum/" + this.props.userID + "/mark=+" + 
            "&year=" + date.getFullYear() + "&month=" + date.getHours() + 1)
            
    }

    render() {

        return(
            <div id="report">

                <img src={graph} alt="Graph" />

                <div>{this.state.currentMonth}</div>
            </div>
        )
    }
}
