import React, {Component} from "react";
import "../css/newPayment.css";
import caretDown from "../images/caret_down.png";
import bank from "../images/bank.png";
import payment from "../images/payment.png";

export default class NewPayment extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            // Parametry nové platby
            payment: {
                name: "",
                mark: "-",
                accountNumber: "",
                amount: 0,
                currency: "CZK",
                variableSymbol: 0,
                constantSymbol: 0,
                specificSymbol: 0,
                paymentDate: new Date(),
                paymentType: "Platba bankovním převodem",
            },

            // Úspěšná platba
            successfulPayment : false,

            // Chybové zprávy
            nameError: "",
            accountNumberError: "",
            amountError: "",
            variableSymbolError: "",
            constantSymbol: "",
            specificSymbol: "",

            // Seznam bankovních kódů
            bankCodes: [],

            // Seznam dospotupných měn
            currencies: [],

            // Prefix čísla účtu
            accountNumberPrefix: "",

            // Bankovní kód
            bankCode: "",
            codeIsSelected: false,

            // Potvrzovací kód
            confirmation: Math.floor(Math.random() * (99999 - 10000 + 1) + 10000),
            confirmationMatch: false,
        }

        this.setBankCode = this.setBankCode.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleConformation = this.handleConformation.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {
        document.title = "Nová platba | Web Bank";

        // Request - vrací kódy bank
        fetch("http://localhost:8080/api/bank-codes").then(response => response.json()
            .then(data => this.setState({
                bankCodes: data,
        })));

        // Request - vrací seznam měn
        fetch("http://localhost:8080/api/currencies").then(response => response.json()
            .then(data => this.setState({
                currencies: data.sort((a, b) => {return a.id - b.id}),
        })));
    }

// Změna stavu kódu banky //////////////////////////////////////////////////////////

    setBankCode(event) {

        // Nastavení kódu banky, při kliknutí
        // Přepnutí true / false
        if (event.target.id === "bankCode") {

            this.setState({
                bankCode: event.target.attributes.name.value,
                codeIsSelected: !this.state.codeIsSelected,
            });

        // Přepnutí true / false
        } else {

            this.setState({
                codeIsSelected: !this.state.codeIsSelected,
            });
        }
    }

// Odeslání formuláře /////////////////////////////////////////////////////////////

    handleSubmit(event) {
        event.preventDefault();

        // Složení bankovního účtu
        const accountNumber = this.state.accountNumberPrefix + "/" + this.state.bankCode;

        this.setState({
            
            payment: {
                ...this.state.payment,
                accountNumber: accountNumber,
            }
        }, () => {

            // Odeslaní platby, při shodném ověřovacém kódu
            if (this.state.confirmationMatch) {
    
                fetch("http://localhost:8080/api/new-payment", {
    
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
    
                    body: JSON.stringify(this.state.payment),
    
                }).then(response => response.json().then(data => {
    
                    // Úspěšná platba
                    if (response.ok) {

                        this.setState({
                            successfulPayment: true,
                        });

                    } else {

                        // Změna stavu chybových zpráv
                        this.setState({
                            nameError: data,
                            accountNumberError: "",
                            amountError: "",
                            variableSymbolError: "",
                            constantSymbol: "",
                            specificSymbol: "",
                        });
                    }
    
                }));
    
            } else {
    
                // Error message
            }
        });
    }

// Ověření kódu //////////////////////////////////////////////////////////////////

    handleConformation(event) {

        const name  = event.target.name;
        const value = parseInt(event.target.value);

        if (value === this.state[name]) {
            
            this.setState({
                confirmationMatch: true,
            });
        }
    }

// Změna stavu zadaných údajů /////////////////////////////////////////////////////

handleChange(event) {

    if (event.target.name === "accountNumberPrefix") {
        
        this.setState({
            [event.target.name]: event.target.value,
        });

    } else {

        this.setState({

            payment: {
                ...this.state.payment,
                [event.target.name]: event.target.value,
            }
        });
    }
}

// Vykreslení /////////////////////////////////////////////////////////////////////

    render() {

        return(
            <div id="newPaymentContainer">

                <form id="newPayment" onSubmit={this.handleSubmit}>

                    <img id="payment" src={payment} alt="Payment" />

                    <h1>Nová platba</h1>
                    <hr/> <br/>

                    <table>
                        <tbody>
                            <tr>
                                <td colSpan="2">
                                    <br/>
                                    <div>Povinné údaje</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="name">Název platby:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.nameError}</div>

                                    <input id="name" 
                                           placeholder="Nájem bytu" 
                                           type="text" 
                                           name="name"
                                           onChange={this.handleChange}
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="accountNumberPrefix">Číslo účtu:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.accountNumberError}</div>

                                    <input id="accountNumberPrefix" 
                                           placeholder="7253962689" 
                                           type="text" 
                                           name="accountNumberPrefix"
                                           onChange={this.handleChange}
                                    />

                                    <div id="bankCodesSelect" onClick={this.setBankCode}>
                                        <div>
                                            <div>{this.state.bankCode}</div>

                                            <img id="bank" 
                                                 src={bank} 
                                                 alt="Bank" 
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />

                                            <img id="caretDown"
                                                 src={caretDown} 
                                                 alt="Caret Down"
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />
                                        </div>

                                        <div id="bankCodesChoice" className={(this.state.codeIsSelected) ? "show" : "hide"}>
                                            {this.state.bankCodes.map((item, index) => 
                                                
                                                <div id="bankCode" key={index}
                                                     onClick={this.setBankCode}
                                                     name={item.code}>
                                                    {item.code} - {item.name}
                                                </div>
                                            )} 
                                        </div>
                                    </div>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="amount">Částka:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.amountError}</div>      

                                    <input id="amount" 
                                           placeholder="0,00" 
                                           type="text"
                                           name="amount"
                                           onChange={this.handleChange}
                                    />

                                    <select name="currency" onChange={this.handleChange}>
                                        {this.state.currencies.map((item, index) =>

                                            <option key={index} name="currency" value={item.code}>
                                                {item.code}
                                            </option>
                                        )}
                                    </select>
                                </td>
                            </tr>

                            <tr>
                                <td colSpan="2">
                                    <br/> <br/>
                                    <div>Nepovinné údaje</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="variableSymbol">Variabilní symbol:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.variableSymbolError}</div>  

                                    <input id="variableSymbol" 
                                           placeholder="5795504032" 
                                           type="text"
                                           name="variableSymbol"
                                           onChange={this.handleChange}
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="constantSymbol">Konstantní symbol:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.constantSymbol}</div>

                                    <input id="constantSymbol" 
                                           placeholder="5296841057" 
                                           type="text"
                                           name="constantSymbol"
                                           onChange={this.handleChange}
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="specificSymbol">Specifický symbol:</label>
                                </td>

                                <td>
                                    <div id="error">{this.state.specificSymbol}</div>

                                    <input id="specificSymbol" 
                                           placeholder="4398956257" 
                                           type="text"
                                           name="specificSymbol"
                                           onChange={this.handleChange} 
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td colSpan="2">
                                    <br/> <br/>
                                    <div>Ověření</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="confirmation">Ověřovací kód:</label>
                                </td>

                                <td>
                                    <input id="confirmation" 
                                           type="text"
                                           name="confirmation"
                                           onChange={this.handleConformation} 
                                    />

                                    <div id="confirmationValue">{this.state.confirmation}</div>
                                </td>
                            </tr>
                        </tbody>
                    </table>

                    <button type="submit">Odeslat platbu</button>
                </form>
            </div>
        )
    }
}
