import React, {Component} from "react";
import "../css/newPayment.css";
import caretDown from "../images/caret_down.png";
import bank from "../images/bank.png";
import payment from "../images/payment.png";

export default class NewPayment extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        const date = new Date();

        this.state = {

            //
            payment: {
                name: "",
                mark: "-",
                amount: 0,
                currency: "",
                variableSymbol: 0,
                constantSymbol: 0,
                specificSymbol: 0,
                paymentDate: new Date().toISOString(),
            },

            // Seznam bankovních kódů
            bankCodes: [],

            // Seznam dospotupných měn
            currencies: [],

            // Bankovní kód
            bankCode: "",
            codeIsSelected: false,

            // Potvrzovací kód
            confirmation: Math.floor(Math.random() * 99999) + 10000,
            confirmationMatch: false,
        }

        this.setBankCode = this.setBankCode.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleConformation = this.handleConformation.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {
        document.title = "Nová platba | Web Bank";

        // Request - vrací kódy bank
        fetch("http://localhost:8080/api/bank-codes").then(response => response.json()
            .then(data => this.setState({
                bankCodes: data,
        })));

        // Request - vrací seznam měn
        fetch("http://localhost:8080/api/currencies").then(response => response.json()
            .then(data => this.setState({
                currencies: data.sort((a, b) => {return a.id - b.id}),
        })));
    }

// Změna stavu kódu banky //////////////////////////////////////////////////////////

    setBankCode(event) {

        // Nastavení kódu banky, při kliknutí
        // Přepnutí true / false
        if (event.target.id === "bankCode") {

            this.setState({
                bankCode: event.target.attributes.name.value,
                codeIsSelected: !this.state.codeIsSelected,
            });

        // Přepnutí true / false
        } else {

            this.setState({
                codeIsSelected: !this.state.codeIsSelected,
            });
        }
    }

// Odeslání formuláře /////////////////////////////////////////////////////////////

    handleSubmit(event) {
        event.preventDefault();

        //
    }

// Ověření kódu //////////////////////////////////////////////////////////////////

    handleConformation(event) {

        const name  = event.target.name;
        const value = parseInt(event.target.value);

        if (value === this.state[name]) {
            
            this.setState({
                confirmationMatch: true,
            });
        }
    }

// Vykreslení /////////////////////////////////////////////////////////////////////

    render() {

        console.log(this.state.confirmationMatch);

        return(
            <div id="newPaymentContainer">

                <div id="newPayment">

                    <img id="payment" src={payment} alt="Payment" />

                    <h1>Nová platba</h1>
                    <hr/> <br/>

                    <table>
                        <tbody>
                            <tr>
                                <td colSpan="2">
                                    <br/>
                                    <div>Povinné údaje</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="name">Název platby:</label>
                                </td>

                                <td>
                                    <input id="name" 
                                           placeholder="Nájem bytu" 
                                           type="text" 
                                           name="name"
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="accountNumber">Číslo účtu:</label>
                                </td>

                                <td>
                                    <input id="accountNumber" 
                                           placeholder="7253962689" 
                                           type="text" 
                                           name="accountNumber"
                                    />

                                    <div id="bankCodesSelect" onClick={this.setBankCode}>
                                        <div>
                                            <div>{this.state.bankCode}</div>

                                            <img id="bank" 
                                                 src={bank} 
                                                 alt="Bank" 
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />

                                            <img id="caretDown"
                                                 src={caretDown} 
                                                 alt="Caret Down"
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />
                                        </div>

                                        <div id="bankCodesChoice" className={(this.state.codeIsSelected) ? "show" : "hide"}>
                                            {this.state.bankCodes.map((item, index) => 
                                                
                                                <div id="bankCode" key={index}
                                                     onClick={this.setBankCode}
                                                     name={item.code}>
                                                    {item.code} - {item.name}
                                                </div>
                                            )} 
                                        </div>
                                    </div>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="amount">Částka:</label>
                                </td>

                                <td>
                                    <input id="amount" 
                                           placeholder="0,00" 
                                           type="text"
                                           name="amount"
                                    />

                                    <select>
                                        {this.state.currencies.map((item, index) =>
                                            <option key={index}>{item.code}</option>
                                        )}
                                    </select>
                                </td>
                            </tr>

                            <tr>
                                <td colSpan="2">
                                    <br/> <br/>
                                    <div>Nepovinné údaje</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="variableSymbol">Variabilní symbol:</label>
                                </td>

                                <td>
                                    <input id="variableSymbol" 
                                           placeholder="5795504032" 
                                           type="text"
                                           name="variableSymbol"
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="constantSymbol">Konstantní symbol:</label>
                                </td>

                                <td>
                                    <input id="constantSymbol" 
                                           placeholder="6269" 
                                           type="text"
                                           name="constantSymbol"
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="specificSymbol">Specifický symbol:</label>
                                </td>

                                <td>
                                    <input id="specificSymbol" 
                                           placeholder="4398956257" 
                                           type="text"
                                           name="specificSymbol" 
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td colSpan="2">
                                    <br/> <br/>
                                    <div>Ověření</div>
                                    <hr/>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="confirmation">Ověřovací kód:</label>
                                </td>

                                <td>
                                    <input id="confirmation" 
                                           type="text"
                                           name="confirmation"
                                           onChange={this.handleConformation} 
                                    />

                                    <div id="confirmationValue">{this.state.confirmation}</div>
                                </td>
                            </tr>
                        </tbody>
                    </table>

                    <button type="submit">Odeslat platbu</button>
                </div>
            </div>
        )
    }
}
