import React, {Component} from "react";

import logo from "../images/logo.png";
import "../loginPage/loginPage.css";

export default class LoginForm extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////////////

    constructor(props) {
        
        super(props)

        this.state = {

            // Přihlašovací údaje
            clientNumber: "",
            password: "",

            // Chybové zprávy
            clientNumberError: "",
            passwordError: "",

            // Uživatel
            user: {
                
                name: "",
                surname: "",
                email: "",
                clientNumber: 0,
                password: "",
                balance: 0,
                currency: "",
                accountNumber: "",
            }
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

// Změna stavu ///////////////////////////////////////////////////////////////////////

    handleChange(event) {

        this.setState({
            [event.target.name]: event.target.value
        });
    }

// Odeslání formuláře ////////////////////////////////////////////////////////////////

    handleSubmit(event) {

        event.preventDefault();

        fetch("http://localhost:8080/api/login", {

            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(this.state)
            
        }).then(response => response.json().then(data => {

            if (response.ok) {

                console.log("Response Ok");
                console.log(data.user);

                this.setState({
                    user: data.user,
                });

            } else {

                this.setState({
                    clientNumberError: data.clientNumber,
                    passwordError: data.password,
                });
            }
        }));

        //     if (response.ok) {

                

        //     } else response.json().then(data => this.setState({

        //         clientNumberError: data.clientNumber,
        //         passwordError: data.password,
        //     }));
        // });
    }

// Vykreslení ////////////////////////////////////////////////////////////////////////

    render() {

        return (
            <form onSubmit={this.handleSubmit}>

                <img id="logo" src={logo} alt="Logo" />

                <table>
                    <tr>
                        <td>
                            <label htmlFor="clientNumber">Klientské číslo: </label>
                        </td>

                        <td>
                            <div id="error">{this.state.clientNumberError}</div>

                            <input id="clientNumber" name="clientNumber" autoFocus="autoFocus" 
                                onChange={this.handleChange} />
                        </td>
                    </tr>

                    <tr>
                        <td>
                            <label htmlFor="password">Heslo: </label>
                        </td>

                        <td>
                            <div id="error">{this.state.passwordError}</div>

                            <input type="password" name="password" id="password"
                                onChange={this.handleChange} />
                        </td>
                    </tr>
                    
                    <tr>
                        <td colSpan="2">
                            <button id="submit" type="submit">Pokračovat</button>
                        </td>
                    </tr>
                </table>
            </form>
        )
    }
}
