            })));
    }

    render() {

        return(
            <div id="report">

                <img src={graph} alt="Graph" />

                <div>{this.state.currentMonth}</div>

                <div>+{this.state.income}</div>
                <div>-{this.state.costs}</div>
            </div>
        )
    }
}
