import React, {Component} from "react";

import creditCard from "../images/credit_card.png";

export default class CreditCardInfo extends Component {

// Konstruktor ///////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {
            creditCard: {
                id: 0,
                cardNumber: 0,
                validFrom: "",
                validTo: "",
                type: "",
            },
        }
    }

// Získání dat ///////////////////////////////////////////////////////////////

    componentDidMount() {

        // Request - vrací kreditní kartu, podle ID uživatele
        fetch("http://localhost:8080/api/credit-card/" + this.props.userID)
            .then(response => response.json().then(data => this.setState({
                creditCard: data,
            })));
    }

// Vykreslení ////////////////////////////////////////////////////////////////

    render() {

        return(
            <div id="creditCard">
                
                <img src={creditCard} alt="Credit Card" />

                <div id="type">{this.state.type}</div>
                
                <div id="validFrom">Platí od: <b>{this.state.validFrom}</b></div>
                <div id="validTo">Platí do: <b>{this.state.validTo}</b></div>

                <div>Číslo karty:
                    <div id="cardNumber">{}</div>
                </div>
            </div>
        )
    }

}
