import React, {Component} from "react";
import graph from "../images/graph.png";
import leftArrow from "../images/left_arrow.png";
import rightArrow from "../images/right_arrow.png";

import Months from "./Months";

export default class PaymentsReport extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            selectedMonth: {
                name: "",
                number: 0,
                income: 0,
                costs: 0,
            },
        }

        this.numberFormatter = this.numberFormatter.bind(this);
    }

    componentDidMount() {

        const date = new Date();
        this.setState({
            selectedMonth: Months[date.getMonth() + 1],
        });

        // Request - vrací součet příjmů za zadaného měsíce
        fetch("http://localhost:8080/api/payments/sum/" + this.props.userID + "/mark=+" + 
            "&year=" + date.getFullYear() + "&month=" + (date.getMonth() + 1))
            .then(response => response.json().then(data => this.setState({
                income: data,
        })));

        // Request - vrací součet výdajů za zadaného měsíce
        fetch("http://localhost:8080/api/payments/sum/" + this.props.userID + "/mark=-" + 
            "&year=" + date.getFullYear() + "&month=" + (date.getMonth() + 1))
            .then(response => response.json().then(data => this.setState({
                costs: data,
        })));
    }

// Naformátování čísla ///////////////////////////////////////////////////////////

    numberFormatter(number) {
        return number.toString().replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, " ")
            .replace(".", ",");
    }

// Vykreslení ////////////////////////////////////////////////////////////////////

    render() {

        const balance = this.state.income - this.state.costs;
        const mark = (balance > 0 ? "+" : "-");
        const currentMonth = new Date().getMonth() + 1;

        console.log(currentMonth);
        console.log(this.state.currentMonth);

        return(
            <div id="report">

                <img src={graph} alt="Graph" />

                <div id="month">
                    <img id="leftArrow" src={leftArrow} alt="Left Arrow" />

                    {this.state.currentMonth}

                    <img id="rightArrow" className={(this.state.currentMonth === currentMonth ? "hide" : "")} src={rightArrow} alt="Right Arrow" />
                </div>

                <div id="income">
                    +{this.numberFormatter(this.state.income.toFixed(2))} CZK
                </div>

                <div id="costs">
                    -{this.numberFormatter(this.state.costs.toFixed(2))} CZK
                </div>

                <div id ="balance" className={(balance > 0 ? "plus" : "minus")}>
                    {mark}{this.numberFormatter(balance.toFixed(2))} CZK
                </div>
            </div>
        )
    }
}
