import React, {Component} from "react";
import "../css/newPayment.css";
import caretDown from "../images/caret_down.png";
import bank from "../images/bank.png";

export default class NewPayment extends Component {

// Konstruktor /////////////////////////////////////////////////////////////////////

    constructor(props) {
        super(props);

        this.state = {

            //
            bankCodes: [],

            //
            bankCode: "",

            //
            codeSelected: false,
        }

        this.setBankCode = this.setBankCode.bind(this);
    }

// Získání dat ////////////////////////////////////////////////////////////////////

    componentDidMount() {
        document.title = "Nová platba | Web Bank";

        fetch("http://localhost:8080/api/bank-codes").then(response => response.json()
            .then(data => this.setState({
                bankCodes: data,
        })));
    }

// 

    setBankCode(event) {
        
        this.setState({
            bankCode: event.target.attributes.name.value,
            codeSelected: !this.state.codeSelected
        });
    }

// Vykreslení /////////////////////////////////////////////////////////////////////

    render() {

        console.log(this.state.codeSelected);

        return(
            <div id="newPaymentContainer">

                <div id="newPayment">

                    <h1>Nová platba</h1>

                    <table>
                        <tbody>
                            <tr>
                                <td>
                                    <label htmlFor="paymentName">Název platby:</label>
                                </td>

                                <td>
                                    <input id="paymentName" 
                                           placeholder="Nájem bytu" 
                                           type="text" 
                                    />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="accountNumber">Číslo účtu:</label>
                                </td>

                                <td>
                                    <input id="accountNumber" 
                                           placeholder="7253962689" 
                                           type="text" 
                                    />

                                    <div id="bankCodesSelect">
                                        <div>
                                            <div>{this.state.bankCode}</div>

                                            <img id="bank" 
                                                 src={bank} 
                                                 alt="Bank" 
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />

                                            <img id="caretDown"
                                                 src={caretDown} 
                                                 alt="Caret Down"
                                                 className={(this.state.bankCode) ? "hide" : ""}
                                            />
                                        </div>

                                        <div id="bankCodesChoice" className={this.state.codeSelected ? "hide" : ""}>
                                            {this.state.bankCodes.map((item, index) => 
                                                
                                                <div key={index}
                                                     onClick={this.setBankCode}
                                                     name={item.code}>
                                                     {item.code} - {item.name}
                                                </div>
                                            )} 
                                        </div>
                                    </div>
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="amount">Částka:</label>
                                </td>

                                <td>
                                    <input id="amount" placeholder="0,00" type="text" />

                                    <select>
                                        <option>CZK</option>
                                    </select>
                                </td>
                            </tr>

                            <tr>
                                <td colSpan="2">
                                    <br />
                                    <div>Nepovinné údaje</div>
                                    <hr />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="variableSymbol">Variabilní symbol:</label>
                                </td>

                                <td>
                                    <input id="variableSymbol" type="text" />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="constantSymbol">Konstantní symbol:</label>
                                </td>

                                <td>
                                    <input id="constantSymbol" type="text" />
                                </td>
                            </tr>

                            <tr>
                                <td>
                                    <label htmlFor="specificSymbol">Specifický symbol:</label>
                                </td>

                                <td>
                                    <input id="specificSymbol" type="text" />
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
